  @property({ type: Boolean }) open: boolean = false;

  @property({ type: String }) wantedWidth: string = "24rem";

  @state() showing: boolean = false;

  @state() private contentActive = false;

  private lastFocus: HTMLElement | null = null;

  close() {
    this.dispatchEvent(new CustomEvent("close"));
    if (this.lastFocus) {
      this.lastFocus.focus();
    }
  }

  private _handleKeyDown = (e: KeyboardEvent) => {
    if (e.key === "Escape" && this.open) {
      this.close();
    }
  };

  connectedCallback() {
    super.connectedCallback();
    window.addEventListener("keydown", this._handleKeyDown);
  }

  disconnectedCallback() {
    window.removeEventListener("keydown", this._handleKeyDown);
    super.disconnectedCallback();
  }

  protected updated(changedProperties: PropertyValues) {
    super.updated(changedProperties);

    if (changedProperties.has("open")) {
      if (this.open) {
        this.showing = true;
        this.contentActive = false; // reset

        this.requestUpdate();
        // Wait for DOM to render before triggering animation
        requestAnimationFrame(() => {
          this.contentActive = true;
        });

        const contentEl = this.shadowRoot?.getElementById("content");
        if (contentEl) {
          this.lastFocus = document.activeElement as HTMLElement;
          contentEl.focus();
        }
      } else {
        this.contentActive = false;
        const contentEl = this.shadowRoot?.getElementById("content");
        if (contentEl) {
          contentEl.classList.remove("open");
          contentEl.classList.add("closing");
        }
        setTimeout(() => {
          this.showing = false;
          this.requestUpdate();
          if (this.lastFocus) this.lastFocus.focus();
        }, 400);
      }
    }
  }

